import React, { useEffect, useState } from "react";
import ReCAPTCHA from "react-google-recaptcha";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  Col,
  Container,
  Form,
  FormFeedback,
  FormGroup,
  Input,
  Label,
  Row,
} from "reactstrap";
import { logout, register } from "../actions/userActions";
import { onlyChar, onlyPhone } from "../data/Regex";
import Select from "react-select";
import bcrypt from "bcryptjs";  // **Ensure the correct package is imported**

function RegisterScreen() {
  const navigate = useNavigate();

  const [userName, setUserName] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [pass, setPass] = useState("");
  const [address, setAddress] = useState("");
  const [category, setCategory] = useState(null);
  const [phoneNo, setPhoneNo] = useState("");

  const dispatch = useDispatch();

  const userRegister = useSelector((state) => state.userRegister);
  const { userInfo } = userRegister;

  const [pwdError, setPwdError] = useState(false);
  const [phError, setPhError] = useState(false);
  const [unameError, setUnameError] = useState(false);
  const [fnameError, setFnameError] = useState(false);
  const [lnameError, setLnameError] = useState(false);
  const [emailError, setEmailError] = useState(false);
  const [addressError, setAddressError] = useState(false);

  useEffect(() => {
    if (userInfo) {
      localStorage.clear();
      dispatch(logout());
      navigate("/login");
    }
  }, [userInfo, navigate, dispatch]);

  const submitHandler = async (e) => {
    e.preventDefault();

    let isValid = true;

    if (!onlyChar.test(userName)) {
      setUnameError(true);
      isValid = false;
    } else {
      setUnameError(false);
    }

    if (!onlyChar.test(firstName)) {
      setFnameError(true);
      isValid = false;
    } else {
      setFnameError(false);
    }

    if (!onlyChar.test(lastName)) {
      setLnameError(true);
      isValid = false;
    } else {
      setLnameError(false);
    }

    if (pass.length < 6) {
      setPwdError(true);
      isValid = false;
    } else {
      setPwdError(false);
    }

    if (address.length < 25) {
      setAddressError(true);
      isValid = false;
    } else {
      setAddressError(false);
    }

    if (!onlyPhone.test(phoneNo)) {
      setPhError(true);
      isValid = false;
    } else {
      setPhError(false);
    }

    if (isValid) {
      try {
        const hashedPassword = await bcrypt.hash(pass, 10);

        dispatch(
          register(
            userName,
            firstName,
            lastName,
            email,
            hashedPassword,  // **Use the hashed password here**
            address,
            phoneNo,
            category?.value // Use optional chaining to safely access value
          )
        );
      } catch (error) {
        console.error("Error hashing password", error);
      }
    }
  };

  const reset = () => {
    setUserName("");
    setFirstName("");
    setLastName("");
    setAddress("");
    setPass("");
    setEmail("");
    setCategory(null);
    setPhoneNo("");

    // Reset errors
    setPhError(false);
    setPwdError(false);
    setFnameError(false);
    setLnameError(false);
    setUnameError(false);
    setEmailError(false);
    setAddressError(false);
  };

  const options = [
    { value: 2, label: "Instructor" },
    { value: 3, label: "Student" },
  ];

  const [verified, setVerified] = useState(false);

  const onChange = (value) => {
    console.log("Captcha value:", value);
    setVerified(true);
  };

  return (
    <Container className="my-5">
      <Row className="pt-5">
        <Col sm={{ size: 6, offset: 3 }}>
          <Card color="dark" outline>
            <CardHeader className="text-center">
              <h3>Register</h3>
            </CardHeader>
            <CardBody>
              <Form onSubmit={submitHandler}>
                <FormGroup>
                  <Label for="userName">User Name</Label>
                  <Input
                    id="userName"
                    placeholder="Enter user name"
                    type="text"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                    autoCapitalize="none"
                    autoComplete="off"
                    style={{ textTransform: 'none' }}
                  />
                  {unameError && (
                    <p style={{ color: "red" }}>Enter a valid username.</p>
                  )}
                </FormGroup>
                <FormGroup>
                  <Label for="firstName">First Name</Label>
                  <Input
                    id="firstName"
                    placeholder="Enter first name"
                    type="text"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    autoCapitalize="none"
                    autoComplete="off"
                    style={{ textTransform: 'none' }}
                  />
                  {fnameError && (
                    <p style={{ color: "red" }}>Only text data allowed.</p>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label for="lastName">Last Name</Label>
                  <Input
                    id="lastName"
                    placeholder="Enter last name"
                    type="text"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    autoCapitalize="none"
                    autoComplete="off"
                    style={{ textTransform: 'none' }}
                  />
                  {lnameError && (
                    <p style={{ color: "red" }}>Only text data allowed.</p>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label for="userEmail">Email</Label>
                  <Input
                    id="userEmail"
                    placeholder="Enter email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  {emailError && (
                    <p style={{ color: "red" }}>Enter a valid email.</p>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label for="userPassword">Password</Label>
                  <Input
                    id="userPassword"
                    placeholder="Enter password"
                    type="password"
                    value={pass}
                    onChange={(e) => setPass(e.target.value)}
                  />
                  {pwdError && (
                    <p style={{ color: "red" }}>Password must be at least 6 characters.</p>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label for="phoneNo">Phone No.</Label>
                  <Input
                    id="phoneNo"
                    placeholder="Enter phone number"
                    type="text"
                    value={phoneNo}
                    onChange={(e) => setPhoneNo(e.target.value)}
                  />
                  {phError && (
                    <p style={{ color: "red" }}>Please enter a valid phone number.</p>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label for="userAddress">Address</Label>
                  <Input
                    id="userAddress"
                    type="textarea"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                  />
                  {addressError && (
                    <p style={{ color: "red" }}>
                      Address must be at least 25 characters.
                    </p>
                  )}
                </FormGroup>

                <FormGroup>
                  <Label for="category">Select Role</Label>
                  <Select
                    value={category}
                    onChange={(item) => setCategory(item)}
                    options={options}
                    placeholder="Select Category"
                    required
                  />
                </FormGroup>

                <div style={{ marginLeft: "13px", marginBottom: "20px" }}>
                  <ReCAPTCHA
                    className="col-sm-8"
                    sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
                    onChange={onChange}
                  />
                </div>

                <Container className="text-center">
                  <Button type="submit" color="success" disabled={!verified}>
                    Submit
                  </Button>
                  <Button type="button" onClick={reset} color="success">
                    Reset
                  </Button>
                </Container>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>
    </Container>
  );
}

export default RegisterScreen;
